#include <stdio.h>

#define PI 3.14
#define MULTIPLY(a, b) a *b

enum Status
{
	MARRIED,
	SINGLE
};

struct Person
{
	char name[20];
	int age;
	enum Status status;
};

void print_person(struct Person *p_person)
{
	printf("Person data:\nName: %s\nAge: %d\nStatus: %d\n",
		   (*p_person).name,
		   (*p_person).age,
		   (*p_person).status);
}

